var createUser = function (username, password) { var password = password;
	return {
		username: username,
		changePassword: function(oldPassword,newPassword) {
			if(oldPassword === password) {
				password = newPassword;
			}
		},
		checkPassword: function (attempt) {
			return password === attempt;
		},
	}
}
const billy = createUser('billy', 'fluffy');
console.log(billy.username);
console.log(billy.password)

let isCorrect = billy.checkPassword('fluffy')
billy.changePassword();

